    var floor = 0
    var position = 0
    var firstBasementPosition = 0

    override fun part1(): String {
        return calculateFloor(true)
    }

    override fun part2(): String {
        return calculateFloor(false)
    }

    fun calculateFloor(isPartOne: Boolean): String {
        input.asSequence().forEach { char ->
            position++
            when (char) {
                '(' -> floor++
                ')' -> floor--
            }
            if (floor < 0 && firstBasementPosition == 0) {
                firstBasementPosition = position
            }
        }
        return if (isPartOne) floor.toString() else firstBasementPosition.toString()
